chrome.alarms.onAlarm.addListener((e=>{console.log("Alarm triggered:",e.name),chrome.notifications.create({type:"basic",iconUrl:"/assets/note.png",title:"Alarm Triggered!",message:`Alarm "${e.name}" fired at ${(new Date).toLocaleTimeString()}`,silent:!1}),chrome.windows.create({url:chrome.runtime.getURL("alarm.html"),type:"popup",width:400,height:300})}));